(2, 91, '404 Page Not Found', '404-page-not-found', '<div class="clearfix">&nbsp;</div>\r\n<div class="row">\r\n<div class="col-xs-12 col-sm-4 col-sm-push-8 text-center">\r\n<h1 class="ot-title" style="font-size: 100px;">404</h1>\r\n</div>\r\n<div class="col-xs-12 col-sm-8 col-sm-pull-4">\r\n<h3 class="ot-title" style="font-size: 50px;">OOPS!</h3>\r\n<h4 class="text-uppercase ot-color">We are really sorry but the page you requested cannot be found.</h4>\r\n<p>It seems that the page you were looking for doesn''t exist any more, or maybe it has just moved. Please start again from the home page or feel free to contact us and we will try to fix the problem.</p>\r\n<p>Thank you very much.</p>\r\n</div>\r\n</div>\r\n<div class="clearfix">&nbsp;</div>', '', 1, 2, '2014-06-25 03:14:37', 893, '', '2014-06-25 03:56:04', 294, 0, '0000-00-00 00:00:00', '2014-06-25 03:14:37', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"","show_tags":"0","show_intro":"0","info_block_position":"","show_category":"0","link_category":"","show_parent_category":"0","link_parent_category":"","show_author":"0","link_author":"","show_create_date":"0","show_modify_date":"0","show_publish_date":"0","show_item_navigation":"0","show_icons":"0","show_print_icon":"0","show_email_icon":"0","show_vote":"0","show_hits":"0","show_noauth":"0","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 9, 5, '', '', 1, 65, '{"robots":"noindex, nofollow","author":"","rights":"","xreference":""}', 0, '*', ''),
